import React ,{useState}from 'react';
import AddUser from './components/User/AddUser';
import UsersList from './components/User/UsersList';
// import UserRole from './components/User/UserRole';
import AppSys from './components/Layout/AppSys';
import Menu from './components/UI/Menu';
// import Card from '../components/UI/Card';

function App() {
  // const [userList,setUserList] = useState(["dori","shiri","rami"]);
  const [role,setRole] = useState();
  const [userList,setUserList] = useState([{
    name:"dori",
    role:['ADM','WRK']
  },
  {
    name:"shiri",
    role:['WRK']
  },
  {
    name:"rami",
    role:['WRK']
  }
]);

// userList = ["dori","shiri","rami"];
  // let 
  const onSuccessLogin = (x,user)=>{
    console.log(x); 
    setRole(x);
    console.log(role);
     role>1?<Menu roleList={user.role}/>:<AppSys role={role}/>
  }

  const addUserHandler = (uName,uRole) => {
    console.log(uName);
    setUserList((prevList)=>{
      return [...prevList,{user:uName,role:uRole}];
      // return [...prevList,{user:uName,age:uAge}];
    })
  }

  return (
    <div>
      <AddUser AddUser={addUserHandler} users={userList} success={onSuccessLogin}/>
      <UsersList users={userList}/>
      {/* {role>1?<Menu />:<AppSys />} */}
    </div>
  );
}

export default App;
