import React ,{useState}from 'react';
import AddUser from './components/User/AddUser';
import UsersList from './components/User/UsersList';
import UserRole from './components/User/UserRole';
import AppSys from './components/Layout/AppSys';
// import Card from '../components/UI/Card';

function App() {
  const [userList,setUserList] = useState(["dori","shiri","rami"]);
  const [role,setRole] = useState();
//   const userList = [{
//     name:"dori",
//     role:['ADM','WRK']
//   },
//   {
//     name:"shiri",
//     role:['WRK']
//   },
//   {
//     name:"rami",
//     role:['WRK']
//   }
// ];

// userList = ["dori","shiri","rami"];

  const onSuccessLogin = (x)=>{
     setRole(x);
  }

  const addUserHandler = (uName,uAge) => {
    console.log(uName);
    setUserList((prevList)=>{
      return [...prevList,{user:uName,age:uAge}];
    })
  }

  return (
    <div>
      <AddUser AddUser={addUserHandler} users={userList} success={onSuccessLogin}/>
      <UsersList users={userList}/>
      {role>1?<UserRole />:<AppSys />}
    </div>
  );
}

export default App;
