import React from "react";
// import classes from './Menu.modules.css';
import Card from '../UI/Card';

const Menu = (props) =>{
    // const style={
    //     margin: "2rem auto",
    //     width: "90%",
    //     maxWidth: "40rem",
    //     backgroundColor: "cyan"
    // }

    return (
        // <div>
        //     <p>This is our new menu</p>
        // </div>

        <Card style={{margin: "2rem auto",maxWidth:"100px"}}>
            <ul style={{listStyle:  "none",backgroundColor: "cyan"}}>
                {props.roleList.map((role,ind)=>{
                    console.log(role);
                    return(<li style={{border: "1px solid #aaa",
                        margin: "0.5rem 0",
                        padding: "0.5rem"}} key={ind}><input type="radio" name="radio"/>{role}</li>)
                })}
            </ul>
        </Card>
        // <form className={classes.menu}>
            
        // </form>
    )
}

export default Menu;
