import React ,{useState} from 'react';
import Card from '../UI/Card';
import Button from '../UI/Button';
import classes from './AddUser.module.css';

const AddUser = props => {

    const [enteredName,setEnteredName] = useState('');
    const [ageName,setAgeName] = useState('');

    const addUserHandler = (event) =>{
        event.preventDefault();
        console.log(enteredName,ageName);
        enteredName = '';
        setEnteredName(enteredName);
        console.log(enteredName);
        setAgeName('');
    }

    const onCangeUserHandler = (event) =>{
        setEnteredName(event.target.value);
    }

    const onCangeAgeHandler = (event) =>{
        setAgeName(event.target.value);    
    }

    return (
    <Card className={classes.input}>
        <form onSubmit={addUserHandler}>
            <label htmlFor="user">User</label>
            <input id="user" type="text" onChange={onCangeUserHandler}></input>
            <label htmlFor="age">Age (Year)</label>
            <input id="age" type="text" onChange={onCangeAgeHandler}></input>
            <Button type="submit" onClick={addUserHandler}>Add User</Button>
        </form>
    </Card>
    )
}

export default AddUser ;
